Bash(#!/bin/bash

PROJECT_ID="PVT_kwHOBJ7Qkc4A8Dsu"
NEW_TYPE_FIELD_ID="PVTSSF_lAHOBJ7Qkc4A8DsuzgwL3gk"
EPIC_OPTION_ID="1c7ffdb6"

# Epic item IDs
EPIC_ITEMS=(
    "PVTI_lAHOBJ7Qkc4A8Dsuzgbv3fI"  # Epic 1
    "PVTI_lAHOBJ7Qkc4A8Dsuzgbv3fg"  # Epic 2
    "PVTI_lAHOBJ7Qkc4A8Dsuzgbv3fo"  # Epic 3
    "PVTI_lAHOBJ7Qkc4A8Dsuzgbv3f8"  # Epic 4
    "PVTI_lAHOBJ7Qkc4A8Dsuzgbv3gY"  # Epic 5
)

echo "Setting Epic type for 5 main epics..."

for item_id in "${EPIC_ITEMS[@]}"; do
    echo "Updating item $item_id..."
    gh api graphql -f query='
    mutation {
      updateProjectV2ItemFieldValue(
        input: {
          projectId: "'$PROJECT_ID'"
          itemId: "'$item_id'"
          fieldId: "'$NEW_TYPE_FIELD_ID'"
          value: {
            singleSelectOptionId: "'$EPIC_OPTION_ID'"
          }
        }
      ) {
        projectV2Item {
          id
        }
      }
    }'
done

echo "Done!")

Bash(# Test with just the first user story's tasks
REPO_OWNER="o2alexanderfedin"
REPO_NAME="Telethon"

# Function to get issue node ID
get_issue_node_id() {
    local issue_num=$1
    gh api repos/$REPO_OWNER/$REPO_NAME/issues/$issue_num --jq .node_id 2>/dev/null
}

# Test linking tasks 47-50 to user story 10
echo "Testing: Linking tasks to User Story #10..."
for task in 47 48 49 50; do
    parent_id=$(get_issue_node_id 10)
    child_id=$(get_issue_node_id $task)

    echo -n "  Task #$task... "

    RESULT=$(gh api graphql -f query='
    mutation {
      addSubIssue(input: {
        issueId: "'$parent_id'"
        subIssueId: "'$child_id'"
      }) {
        issue {
          number
        }
        subIssue {
          number
        }
      }
    }' 2>&1)

    if echo "$RESULT" | grep -q "errors"; then
        echo "Already linked or error"
    else
        echo "✅"
    fi
done)
